import './App.css';
import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Row, Col, Container, Card } from 'reactstrap'
import { Link } from 'react-dom'

function App() {
  const [data, setData] = useState([])

  useEffect(()=>{
    axios(`https://api.punkapi.com/v2/beers`).then(res => {
      console.log(res)
      setData(res.data)
    }).catch(err => console.error(err))
  }, [])

  return (
    <div style={{background: '#f4f4f4'}}>
      <Container>
        <div><Link to="#">Sort by </Link></div>
        <Row>
            {data.map((item, index) => <Col lg={3} md={6} key={index} className='mb-2 p-2'>
              <Card className='p-2'>
                <img src={item.image_url} alt={item.name} height={`50vh`} />
                <h5 style={{textAlign: 'center', paddingTop: '2%', lineHeight: '1.5'}}><b>{item.name}</b></h5><br />
                <p><b>Tagline: {item.tagline}</b></p>
              </Card>
            </Col>)}
        </Row>
      </Container>
    </div>
  );
}

export default App;
